(68, 'PS JURIDISKAIS BIROJS OFERENTS', '<p style="font-size: 13pt; color: rgb(6, 40, 179); padding-left: 0pt;">\r\n	<font color="#0628b3"><span style="font-size: 13pt;">Juridiskās konsultācijas</span></font></p>\r\n<ul>\r\n	<li>\r\n		Konsultācijas komerctiesībās, civiltiesībās, darba tiesībās, administratīvajās tiesībās</li>\r\n	<li>\r\n		Komercdarbībai nepiecie&scaron;amo dokumentu sagatavo&scaron;ana</li>\r\n	<li>\r\n		Kooperatīvo sabiedrību, biedrību un nodibinājumu reģistrācijai nepiecie&scaron;amo dokumentu sagatavo&scaron;ana</li>\r\n	<li>\r\n		Komercsabiedrību un citu juridisko personu reģistrācijas dokumentu grozījumu un papildinājumu sagatavo&scaron;ana</li>\r\n	<li>\r\n		Pieteikumu un prezentāciju sastādī&scaron;ana</li>\r\n	<li>\r\n		Prasības pieteikumu, apelācijas un kasācijas sūdzību sastādī&scaron;ana</li>\r\n	<li>\r\n		Fizisku un juridisku personu uz pilnvaras pamata pārstāvē&scaron;ana, t.sk. tiesvedības procesā</li>\r\n	<li>\r\n		Visu veidu līgumu projektu sastādī&scaron;ana</li>\r\n	<li>\r\n		Komersantu un citu personu juridiska apkalpo&scaron;ana</li>\r\n</ul>\r\n<p>\r\n	<img src="images/uk_r4_c3.png" /></p>\r\n<p style="font-size: 13pt; color: rgb(6, 40, 179); padding-left: 0pt;">\r\n	Kontakti</p>\r\n<p style="line-height: 100%; margin-top: 3pt; margin-bottom: 0px;">\r\n	Kontaktpersona: Aina Nagle - praktizējo&scaron;a juriste, Juris Naglis - praktizējo&scaron;s jurists</p>\r\n<p style="line-height: 100%; margin-top: 3pt; margin-bottom: 0px;">\r\n	Tālrunis: 63021005, 29821408, 29274771</p>\r\n<p style="line-height: 100%; margin-top: 3pt; margin-bottom: 0px;">\r\n	E-pasts: <a href="mailto:jur.n.naglis@tvnet.lv">jur.n.naglis@tvnet.lv</a></p>\r\n<p style="line-height: 100%; margin-top: 3pt; margin-bottom: 0px;">\r\n	Adrese: Pasta iela 45-4, Jelgava, LV-3001, 2.stāvs</p>\r\n'),
